import SwiftUI

struct LoadedImage: View {
    let url: String?
    let placeholder: UIImage?
    
    @State private var fetchedImage: UIImage? = nil
    @State private var hasError: Bool = false
    
    var body: some View {
        GeometryReader { geometry in
            ZStack {
                PlaceholderView(image: placeholder)
                    .frame(width: geometry.size.width, height: geometry.size.height)
                
                if let uiImage = fetchedImage {
                    Image(uiImage: uiImage)
                        .resizable()
                        .scaledToFill()
                        .frame(width: geometry.size.width, height: geometry.size.height)
                        .transition(.opacity)
                }
            }
            .clipped()
            .task(id: url) {
                await loadImage()
            }
        }
    }
    
    private func loadImage() async {
        guard let urlString = url, let requestUrl = URL(string: urlString) else {
            hasError = true
            return
        }
        
        if fetchedImage != nil { return }
        
        var request = URLRequest(url: requestUrl)
        request.httpMethod = "GET"
        request.setValue("Mozilla/5.0 (iPhone; CPU iPhone OS 17_0 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/17.0 Mobile/15E148 Safari/604.1", forHTTPHeaderField: "User-Agent")
        request.setValue("https://www.artic.edu/", forHTTPHeaderField: "Referer")
        
        do {
            let (data, response) = try await URLSession.shared.data(for: request)
            
            if let httpResponse = response as? HTTPURLResponse, httpResponse.statusCode == 200,
               let image = UIImage(data: data) {
                
                await MainActor.run {
                    withAnimation(.easeInOut(duration: 0.5)) {
                        self.fetchedImage = image
                        self.hasError = false
                    }
                }
            } else {
                hasError = true
            }
        } catch {
            hasError = true
        }
    }
}


